<script setup lang="ts">
definePageMeta({ layout: "student" })
const config = useRuntimeConfig();
import { useAuthStore } from "~/stores/useAuthStore";
import dayjs from 'dayjs'
const auth = useAuthStore();
const user = auth.user.value;

const aquatic = ref(0);
const exploring = ref(0);
const level = ref(0);
const exp = ref(0);


// my classes

const { data: statResponse, error: statError } = await useApiFetch(`api/student/userStat/${user.id}`, {});

if (statResponse.value) {

  aquatic.value = statResponse.value.courses;

  exploring.value = statResponse.value.enrollments;

  if (statResponse.value.attend < 10) {
    level.value = 1;
  } else {
    level.value = Math.floor(statResponse.value.attend / 10);
  }

  exp.value = statResponse.value.attend % 10;

}

else {
  if (statError.value) {


  }

}

const timelineClasses = ref({})

const { data: classResponse, error: classError } = await useApiFetch(`api/student/getClasses/${user.id}`, {});

if (classResponse.value) {

  timelineClasses.value = classResponse.value;


} else {
  if (classError.value) {


  }
}


// scrollTo
const scrollStat = ref()
const scrollClasses = ref()


const toStat = async () => {

  await setArchive()

  if (scrollStat.value) {

    const element = scrollStat.value.getBoundingClientRect();


    // Scroll to the element with smooth animation
    window.scrollTo({
      top: element.top + window.scrollY,
      behavior: 'smooth',
    });
  }

};


const toClasses = async () => {

  await setClasses()

  if (scrollClasses.value) {

    const element = scrollClasses.value.getBoundingClientRect();

    // Scroll to the element with smooth animation
    window.scrollTo({
      top: element.top + window.scrollY,
      behavior: 'smooth',
    });
  }

};

// to Top



const toTop = async () => {

  // Scroll to the element with smooth animation
  window.scrollTo({
    top: 0,
    behavior: 'smooth',
  });


};



// showinfo
const showArchive = ref(false);

async function setArchive() {
  showArchive.value = !showArchive.value;
  showClasses.value = false
}

const showClasses = ref(false);

async function setClasses() {
  showClasses.value = !showClasses.value;
  showArchive.value = false
}


const clickOutsideArchive = () => {
  showArchive.value = false;
};

const clickOutsideClasses = () => {
  showClasses.value = false;
};

</script>

